import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    persons: [
      { id: 'ewre1', name: 'Max', age: 28 },
      { id: 'ewre2', name: 'Manu', age: 29 },
      { id: 'ewre3', name: 'Stephanie', age: 26 }
    ],
    otherState: 'some other value',
    displayNames : false
  };

  toggleUsersHandler = ()=> {
    const doesShow = this.state.displayNames;
    this.setState({displayNames : !doesShow});
  }

  deletePersonHandler = (personIndex) => {
    //const persons = this.state.persons.slice();//Slice with no arguments simply creates a copy of the array
    //You can use the spread operator instead
    const persons = [...this.state.persons];
    persons.splice(personIndex, 1);
    this.setState({persons : persons});
  }

  nameChangedHandler = (event, id) => {
    const personIndex = this.state.person.findIndex(p =>{
      return p.id === id;
    });

    const person = {
      ...this.state.persons[personIndex]
    };

   // const person = Object.assign({}, this.state.persons[personIndex]); //another possible way



    // console.log('Was clicked!');
    // DON'T DO THIS: this.state.persons[0].name = 'Maximilian';
    this.setState({
      persons: [
        { name: 'Max', age: 28 },
        { name: event.target.value, age: 29 },
        { name: 'Stephanie', age: 26 }
      ]
    });
  };
//This way may be inneficient () => this.switchNameHandler("Maximiliano!!!")
  render() {
    const buttonOneStyle = {
      backgroundColor:'white',
      font:'inherit',
      border:'1px solid blue',
      padding:'8px',
      cursor:'pointer'
    };

    let persons = null;
    if(this.state.displayNames){
      persons = (
      <div>
        {this.state.persons.map((person, index) => {
         return <Person click={() => this.deletePersonHandler(index)}
          name = {person.name} 
          age = {person.age}
          key = {person.id}
          changed = {(event) => this.nameChangedHandler(event, person.id)} />
        })}
      </div>
      );   

    }


    return (
      <div className="App">
        <h1>Hi, I'm a React App</h1>
        <p>This is really working again!</p>

        <button style= {buttonOneStyle} onClick={this.toggleUsersHandler}>togglePersons</button>

          {persons}



      </div>
    );
    // return React.createElement('div', {className: 'App'}, React.createElement('h1', null, 'Does this work now?'));
  }
}

export default App;
